import React from 'react';

const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length) {
    console.log('Tooltip Data:', payload);
    return (
      <div style={styles.tooltipContainer}>
        <p style={styles.label}>{`${payload[0].value} kg`}</p>
        <p style={styles.label}>{`${payload[0].value} Kcal`}</p>
      </div>
    );
  }

  return null;
};

const styles = {
  tooltipContainer: {
    backgroundColor: 'red',
    padding: '10px',
    borderRadius: '5px',
    color: 'white',
  },
  label: {
    margin: 0,
  },
};

export default CustomTooltip;
