import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {
  getUserById,
  getUserActivityById,
  getUserAverageSession,
  getUserPerformance,
} from "../../services/api";

const Profile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const user = await getUserById(id);
        const activity = await getUserActivityById(id);
        const sessions = await getUserAverageSession(id);
        const performance = await getUserPerformance(id);

        setUserData(user);
        setUserActivity(activity);
        setUserAverageSessions(sessions);
        setUserPerformance(performance);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) return <p>Chargement du profil...</p>;

  if (!userData || !userData.userInfos) {
    console.log("Aucun profil trouvé:", userData);
    return <p>Aucun profil trouvé</p>;
  }

  const { userInfos } = userData;

  return (
    <section>
      <h1>Bonjour {userInfos.firstName}</h1>
      <p>Age: {userInfos.age}</p>
      {/* Affichez d'autres informations ici */}
    </section>
  );
};

export default Profile;
