import React from "react";
import { LineChart, Line, XAxis, ResponsiveContainer, Tooltip } from "recharts";

const LineChartComponent = ({ data }) => {
  const days = ["L", "M", "M", "J", "V", "S", "D"];
  const formattedData = data.map((session) => ({
    ...session,
    day: days[session.day - 1],
  }));

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      return (
        <div
          className="custom-tooltip-linechart"
          style={{
            backgroundColor: "white",
            padding: "5px",
            color: "black",
            fontSize: "10px"
          }}
        >
          <p>{`${payload[0].value} min`}</p>
        </div>
      );
    }

    return null;
  };

  // Fonction pour créer des segments de ligne avec des couleurs légèrement différentes
  const renderCustomizedLine = (props) => {
    const { points, color } = props;

    return (
      <g>
        {points.map((entry, index) => (
          index !== points.length - 1 && (
            <line
              key={`line-${index}`}
              x1={entry.x}
              y1={entry.y}
              x2={points[index + 1].x}
              y2={points[index + 1].y}
              stroke={color}
              strokeWidth={2}
            />
          )
        ))}
      </g>
    );
  };

  return (
    <ResponsiveContainer width="100%" height="100%">
      <LineChart data={formattedData}>
        <XAxis
          dataKey="day"
          tickLine={false}
          axisLine={false}
          tick={{ fill: "white" }}
          fontSize={"10px"}
        />
        <Tooltip content={<CustomTooltip />} cursor={false} />

        {/* Création de plusieurs segments de ligne avec des couleurs légèrement différentes */}
        <Line
          type="monotone"
          dataKey="sessionLength"
          dot={false}
          activeDot={{ r: 4 }}
          isAnimationActive={false}
          strokeWidth={0} // Désactivez la ligne par défaut
          stroke="#FFFFFF" // Couleur de base de la ligne
          legendType="none"
          animationDuration={0}
          data={data}
          name="Sessions"
        />

        {/* Ligne avec le dégradé simulé */}
        {formattedData.map((entry, index) => (
          <Line
            key={`line-${index}`}
            dataKey="sessionLength"
            data={[entry]}
            stroke={`rgba(255, 255, 255, ${index / (formattedData.length - 1)})`} // Utilisation de l'index pour créer un effet de dégradé
            dot={false}
            legendType="none"
            isAnimationActive={false}
            strokeWidth={2}
            animationDuration={0}
            type="monotone"
            activeDot={{ r: 4 }}
            connectNulls
            fill="none"
            render={renderCustomizedLine}
          />
        ))}

        {/* Exemple de texte avec 'session' sur une autre ligne */}
        <text x="10" y="20" fill="lightgrey" textAnchor="start" fontSize={10}>
          Durée moyenne des <tspan x="10" dy="12">sessions</tspan>
        </text>
      </LineChart>
    </ResponsiveContainer>
  );
};

export default LineChartComponent;
