import React from "react";
import "./sidecard.css";

const SideCard = ({ type }) => {
  // Tableau des données pour les SideCards
  const sideCardsData = [
    {
      type: "calories",
      icon: require("../../assets/images/sidecard-icons/calories.svg").default,
      label: "Calories",
      bgColor: "#FFEBE5",
    },
    {
      type: "proteines",
      icon: require("../../assets/images/sidecard-icons/proteines.svg").default,
      label: "Protéines",
      bgColor: "#E5F1FF",
    },
    {
      type: "glucides",
      icon: require("../../assets/images/sidecard-icons/glucides.svg").default,
      label: "Glucides",
      bgColor: "#FFF5DB",
    },
    {
      type: "lipides",
      icon: require("../../assets/images/sidecard-icons/lipides.svg").default,
      label: "Lipides",
      bgColor: "#FFE5E5",
    },
  ];

  const cardData = sideCardsData.find(card => card.type === type);

  if (!cardData) {
    return null; 
  }

  const { icon, label, bgColor } = cardData;

  return (
    <div className="side-card">
      <div className="side-card-icon" style={{ backgroundColor: bgColor }}>
        <img src={icon} alt={label} />
      </div>
      <div className="side-card-content">
        <h3>{label}</h3>
        <p>{type === "calories" ? "500kCal" : "100g"}</p> {/* Exemple de contenu dynamique basé sur le type */}
      </div>
    </div>
  );
};

export default SideCard;
