import React from "react";
import { LineChart, Line, XAxis, ResponsiveContainer, Tooltip, Customized } from "recharts";

const LineChartComponent = ({ data }) => {
  const days = ["L", "M", "M", "J", "V", "S", "D"];
  const formattedData = data.map((session) => ({
    ...session,
    day: days[session.day - 1],
  }));

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      return (
        <div
          className="custom-tooltip-linechart"
          style={{
            backgroundColor: "white",
            padding: "5px",
            color: "black",
            fontSize: "10px",
            fontWeight: "bold",
          }}
        >
          <p>{`${payload[0].value} min`}</p>
        </div>
      );
    }

    return null;
  };

  const renderCustomAxisTick = ({ x, y, payload }) => {
    return (
      <text
        x={x}
        y={y + 10}
        textAnchor="middle"
        fill="#FF8484"
        fontSize="10px"
      >
        {days[payload.value - 1]}
      </text>
    );
  };

  return (
    <ResponsiveContainer width="100%" height="100%">
      <LineChart data={formattedData} margin={{ top: 20, right: 30, left: 20, bottom: 0 }}>
        <defs>
          <linearGradient id="gradientLine" x1="0" y1="0" x2="0" y2="1">
            <stop offset="0%" stopColor="#FF8484" />
            <stop offset="100%" stopColor="white" />
          </linearGradient>
        </defs>
        <XAxis
          dataKey="day"
          tickLine={false}
          axisLine={false}
          tick={renderCustomAxisTick}
        />
        <Tooltip content={<CustomTooltip />} cursor={false} />
        <Line
          type="monotone"
          dataKey="sessionLength"
          stroke="url(#gradientLine)"
          strokeWidth={2}
          dot={false}
          activeDot={{ r: 4 }}
        />
        <text x="10" y="20" fill="#FF8484" textAnchor="start" fontSize={10}>
          Dur√©e moyenne des{" "}
          <tspan x="10" dy="12">
            sessions
          </tspan>
        </text>
      </LineChart>
    </ResponsiveContainer>
  );
};

export default LineChartComponent;
