import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios"; // Import Axios
import {
  getUserData,
  getUserActivity,
  getUserAverageSessions,
  getUserPerformance,
} from "../services/api.";

const Profile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userDataResponse = await axios.get(`/api/user/main?userId=${id}`);
        const activityResponse = await axios.get(`/api/user/activity?userId=${id}`);
        const averageSessionsResponse = await axios.get(`/api/user/average-sessions?userId=${id}`);
        const performanceResponse = await axios.get(`/api/user/performance?userId=${id}`);

        setUserData(userDataResponse.data);
        setUserActivity(activityResponse.data);
        setUserAverageSessions(averageSessionsResponse.data);
        setUserPerformance(performanceResponse.data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>Profile Page for User {userData.userInfos.firstName}</h1>
      {/* Utilisation de userActivity */}
      <div>
        <h2>Activity</h2>
        {userActivity && userActivity.sessions.map((session, index) => (
          <div key={index}>
            <p>Day: {session.day}</p>
            <p>Calories: {session.calories}</p>
            <p>Weight: {session.kilogram}</p>
          </div>
        ))}
      </div>
      {/* Utilisation de userAverageSessions */}
      <div>
        <h2>Average Sessions</h2>
        {userAverageSessions && userAverageSessions.sessions.map((session, index) => (
          <div key={index}>
            <p>Day: {session.day}</p>
            <p>Session Length: {session.sessionLength}</p>
          </div>
        ))}
      </div>
      {/* Utilisation de userPerformance */}
      <div>
        <h2>Performance</h2>
        {userPerformance && userPerformance.data.map((performance, index) => (
          <div key={index}>
            <p>Kind: {userPerformance.kind[performance.kind]}</p>
            <p>Value: {performance.value}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Profile;
