            }}
          />
          <Radar
            dataKey="value"
            fill={`${styleVar.primary500}`}
            fillOpacity={0.7}
            stroke="transparent"
          />
        </RadarChart>
      </ResponsiveContainer>
    </ResponsiveContainer>
  );
};

export default PerformanceRadarChart;
