import React from "react";
import { LineChart, Line, XAxis, YAxis, ResponsiveContainer, Tooltip } from "recharts";

const LineChartComponent = ({ data }) => {
  const days = ["L", "M", "M", "J", "V", "S", "D"];
  const formattedData = data.map((session) => ({
    ...session,
    day: days[session.day - 1],
  }));

  // Calculer la valeur maximale de sessionLength dans vos données
  const maxValue = Math.max(...formattedData.map((item) => item.sessionLength));

  // Calculer le milieu de la valeur maximale pour centrer l'axe Y
  const middleValue = maxValue / 2;

  return (
    <ResponsiveContainer width="100%" height="100%">
      <LineChart data={formattedData}>
        <XAxis
          dataKey="day"
          tickLine={false}
          axisLine={false}
          tick={{ fill: "white" }}
          fontSize={"10px"}
        />
        <YAxis
          tickLine={false}
          axisLine={false}
          tick={{ fill: "white" }}
          fontSize={"10px"}
          domain={[0, maxValue]}
        />
        <Tooltip content={<CustomTooltip />} cursor={false} />
        <Line
          type="monotone"
          dataKey="sessionLength"
          stroke="#FFFFFF"
          strokeWidth={2}
          dot={false}
          activeDot={{ r: 4 }}
        />
        <text
          x="10"
          y="20"
          fill="#FF8181"
          textAnchor="start"
          fontSize={10}
        >
          Durée moyenne des{" "}
          <tspan x="10" dy="12">
            sessions
          </tspan>
        </text>
      </LineChart>
    </ResponsiveContainer>
  );
};

export default LineChartComponent;
