import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

const ScoreCircleComponent = ({ score }) => {
  const data = [
    { name: 'Score', value: score },
    { name: 'Remaining', value: 1 - score }
  ];

  const COLORS = ['#E60000', '#FFF'];

  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100%', 
      backgroundColor: '#f2f2f2', 
      borderRadius: '10px' 
    }}>
      <div style={{ width: '100%', height: 250 }}>
        <ResponsiveContainer>
          <PieChart>
            <Pie
              data={data}
              startAngle={90}
              endAngle={450}
              innerRadius="80%"
              outerRadius="100%"
              paddingAngle={0}
              dataKey="value"
              stroke="none"
            >
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
          </PieChart>
        </ResponsiveContainer>
        <div style={{ 
          position: 'absolute', 
          top: '50%', 
          left: '50%', 
          transform: 'translate(-50%, -50%)', 
          textAlign: 'center', 
          color: '#74798C'
        }}>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{`${score * 100}%`}</div>
          <div style={{ fontSize: '14px' }}>de votre</div>
          <div style={{ fontSize: '14px' }}>objectif</div>
        </div>
      </div>
    </div>
  );
};

export default ScoreCircleComponent;
