import axios from 'axios';

const API_URL = "http://localhost:3000";

const getUserData = async (userId) => {
  try {
    const response = await axios.get(`${API_URL}/user/${userId}`);
    const data = response.data;
    console.log("User Data:", data);
    return data;
  } catch (error) {
    throw new Error("Failed to fetch user data");
  }
};

const getUserActivity = async (userId) => {
  try {
    const response = await axios.get(`${API_URL}/user/${userId}/activity`);
    const data = response.data;
    console.log("User Activity:", data);
    return data;
  } catch (error) {
    throw new Error("Failed to fetch user activity");
  }
};

const getUserAverageSessions = async (userId) => {
  try {
    const response = await axios.get(`${API_URL}/user/${userId}/average-sessions`);
    const data = response.data;
    console.log("User Average Sessions:", data);
    return data;
  } catch (error) {
    throw new Error("Failed to fetch user average sessions");
  }
};

const getUserPerformance = async (userId) => {
  try {
    const response = await axios.get(`${API_URL}/user/${userId}/performance`);
    const data = response.data;
    console.log("User Performance:", data);
    return data;
  } catch (error) {
    throw new Error("Failed to fetch user performance");
  }
};

export {
  getUserData,
  getUserActivity,
  getUserAverageSessions,
  getUserPerformance,
};
