import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios"; // Import Axios
import {
  getUserData,
  getUserActivity,
  getUserAverageSessions,
  getUserPerformance,
} from "../services/api.js";

const Profile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);


  return (
    <div>
      <h1>Profile Page for User {userData.userInfos.firstName}</h1>
      {/* Utilisation de userActivity */}
      <div>
        <h2>Activity</h2>
        {userActivity && userActivity.sessions.map((session, index) => (
          <div key={index}>
            <p>Day: {session.day}</p>
            <p>Calories: {session.calories}</p>
            <p>Weight: {session.kilogram}</p>
          </div>
        ))}
      </div>
      {/* Utilisation de userAverageSessions */}
      <div>
        <h2>Average Sessions</h2>
        {userAverageSessions && userAverageSessions.sessions.map((session, index) => (
          <div key={index}>
            <p>Day: {session.day}</p>
            <p>Session Length: {session.sessionLength}</p>
          </div>
        ))}
      </div>
      {/* Utilisation de userPerformance */}
      <div>
        <h2>Performance</h2>
        {userPerformance && userPerformance.data.map((performance, index) => (
          <div key={index}>
            <p>Kind: {userPerformance.kind[performance.kind]}</p>
            <p>Value: {performance.value}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Profile;
