import React from "react";
import {
  LineChart,
  Line,
  XAxis,
  ResponsiveContainer,
  Tooltip,
  CartesianGrid,
  Text,
} from "recharts";

const LineChartComponent = ({ data }) => {
  const days = ["L", "M", "M", "J", "V", "S", "D"];
  const formattedData = data.map((session) => ({
    ...session,
    day: days[session.day - 1],
  }));

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      return (
        <div className="custom-tooltip-linechart" style={{ backgroundColor: "white", padding: "5px", borderRadius: "5px", color: "black" }}>
          <p>{`${payload[0].value} min`}</p>
        </div>
      );
    }

    return null;
  };

  return (
    <ResponsiveContainer width="100%" height="70%">
      <LineChart data={formattedData}>
        {/* <defs>
          <linearGradient id="colorUv" x1="0" y1="0" x2="0" y2="1">
            <stop offset="5%" stopColor="#8884d8" stopOpacity={0.8} />
            <stop offset="95%" stopColor="#8884d8" stopOpacity={0} />
          </linearGradient>
        </defs> */}
        <XAxis dataKey="day" tickLine={false} axisLine={false} tick={{ fill: "white" }} fontSize={"10px"} />
        <Tooltip content={<CustomTooltip />} cursor={false} />
        <Line
          type="monotone"
          dataKey="sessionLength"
          stroke="#FFFFFF"
          strokeWidth={2}
          dot={false}
          activeDot={{ r: 8 }}
        />
        <Text x={50} y={50} fill="white" textAnchor="start"  color="white">
          Dur√©e moyenne des sessions
        </Text>
      </LineChart>
    </ResponsiveContainer>
  );
};

export default LineChartComponent;
