import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getUserData, getUserActivity, getUserAverageSessions, getUserPerformance } from "../services/api.js";

const Profile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const user = await getUserData(id);
        const activity = await getUserActivity(id);
        const sessions = await getUserAverageSessions(id);
        const performance = await getUserPerformance(id);

        setUserData(user);
        setUserActivity(activity);
        setUserAverageSessions(sessions);
        setUserPerformance(performance);
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>Profile Page for User {userData.userInfos.firstName}</h1>
      {/* Ajoutez le contenu du profil ici */}
      <div>
        <h2>Activity</h2>
        {/* Render userActivity here */}
      </div>
      <div>
        <h2>Average Sessions</h2>
        {/* Render userAverageSessions here */}
      </div>
      <div>
        <h2>Performance</h2>
        {/* Render userPerformance here */}
      </div>
    </div>
  );
};

export default Profile;
