import React from "react";
import { BarChart, Bar, Tooltip, Legend, ResponsiveContainer,  } from "recharts";

const BarChartComponent = ({ data }) => {
  return (
    <ResponsiveContainer width="100%" height={300}>
      <BarChart
        data={data}
        margin={{
          top: 40,
          right: 30,
          left: 20,
          bottom: 5,
        }}
      >
        <Text />
        <Tooltip />
        <Legend
          verticalAlign="top"
          align="right"
          style={{ fontSize: "12px" }}
          formatter={(value, entry, index) => {
            const { color } = entry;
            return (
              <span style={{ color }}>
                {value === "kilogram"
                  ? "Poids (kg)"
                  : "Calories brûlées (kCal)"}
              </span>
            );
          }}
        />

        <Bar dataKey="kilogram" name="Poids (kg)" fill="#000000" />
        <Bar dataKey="calories" name="Calories brûlées (kCal)" fill="#ff2501" />
      </BarChart>
    </ResponsiveContainer>
  );
};

export default BarChartComponent;
