import React from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const LineChartComponent = ({ data }) => {
  if (!data || data.length === 0) {
    return <div>Aucune donn√©e disponible</div>;
  }

  const sessionDays = data[0].sessions.map((session) => session.day);

  return (
    <ResponsiveContainer width="100%" height={300}>
      <LineChart data={data} margin={{ top: 5, right: 20, bottom: 15, left: 10 }}>
        <XAxis dataKey="sessions[0].day" tickFormatter={(tick) => `J${tick}`} />
        <YAxis />
        <Tooltip />
        <Line type="monotone" dataKey="sessions[0].calories" stroke="#558B2F" />
      </LineChart>
    </ResponsiveContainer>
  );
};

export default LineChartComponent;
