import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Text
} from "recharts";

const BarChartComponent = ({ data = [] }) => {
  // Créer un tableau d'indices basés sur la longueur des données
  const indexedData = data.map((entry, index) => ({
    ...entry,
    day: index + 1
  }));

  return (
    <ResponsiveContainer width="100%" height={300}>
      <BarChart 
        data={indexedData}
        margin={{
          top: 40,
          right: 30,
          left: 20,
          bottom: 5,
        }}
      >
        {/* Ajout du titre */}
        <Text 
          x={20} 
          y={20} 
          style={{ fontSize: '18px', fontWeight: 'bold' }}
        >
          Activité quotidienne
        </Text>

        {/* Configuration de l'axe des X */}
        <XAxis 
          dataKey="day" 
          tickFormatter={(tick) => tick.toString()} 
          label={{ value: '',  }}
        />

        <YAxis />
        <Tooltip />

        {/* Ajout de la légende */}
        <Legend 
          verticalAlign="top" 
          align="right"
          formatter={(value, entry, index) => {
            const { color } = entry;
            return <span style={{ color }}>{value === 'kilogram' ? 'Poids (kg)' : 'Calories brûlées (kCal)'}</span>;
          }}
        />

        <Bar dataKey="kilogram" name="Poids (kg)" fill="#000000" />
        <Bar dataKey="calories" name="Calories brûlées (kCal)" fill="#ff2501" />
      </BarChart>
    </ResponsiveContainer>
  );
};

export default BarChartComponent;
