import axios from "axios";
import {
  USER_MAIN_DATA,
  USER_ACTIVITY,
  USER_AVERAGE_SESSIONS,
  USER_PERFORMANCE,
} from "./mockData.js"; // Ensure correct path to mock data

const getUserData = (userId) => {
  return axios
    .get("/api/user/main", { params: { userId } })
    .then((response) => response.data)
    .catch((error) => {
      console.error("Error fetching user data:", error);
      throw error;
    });
};

const getUserActivity = (userId) => {
  return axios
    .get("/api/user/activity", { params: { userId } })
    .then((response) => response.data)
    .catch((error) => {
      console.error("Error fetching user activity:", error);
      throw error;
    });
};

const getUserAverageSessions = (userId) => {
  return axios
    .get("/api/user/average-sessions", { params: { userId } })
    .then((response) => response.data)
    .catch((error) => {
      console.error("Error fetching user average sessions:", error);
      throw error;
    });
};

const getUserPerformance = (userId) => {
  return axios
    .get("/api/user/performance", { params: { userId } })
    .then((response) => response.data)
    .catch((error) => {
      console.error("Error fetching user performance:", error);
      throw error;
    });
};

export { getUserData, getUserActivity, getUserAverageSessions, getUserPerformance };
