import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getUserData, getUserActivity, getUserAverageSessions, getUserPerformance } from "../services/api.js";

const Profile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState(null);
  const [userActivity, setUserActivity] = useState(null);
  const [userAverageSessions, setUserAverageSessions] = useState(null);
  const [userPerformance, setUserPerformance] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const user = await getUserData(id);
        const activity = await getUserActivity(id);
        const sessions = await getUserAverageSessions(id);
        const performance = await getUserPerformance(id);

        setUserData(user);
        setUserActivity(activity);
        setUserAverageSessions(sessions);
        setUserPerformance(performance);
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>Profile Page for User {userData.userInfos.firstName}</h1>

      <div>
        <h2>Activity</h2>
        {userActivity && (
          <div>
            {userActivity.sessions.map((session, index) => (
              <div key={index}>
                <p>Day: {session.day}</p>
                <p>Calories: {session.calories}</p>
                <p>Weight: {session.kilogram}</p>
              </div>
            ))}
          </div>
        )}
      </div>

      <div>
        <h2>Average Sessions</h2>
        {userAverageSessions && (
          <div>
            {userAverageSessions.sessions.map((session, index) => (
              <div key={index}>
                <p>Day: {session.day}</p>
                <p>Session Length: {session.sessionLength}</p>
              </div>
            ))}
          </div>
        )}
      </div>

      <div>
        <h2>Performance</h2>
        {userPerformance && (
          <div>
            {userPerformance.data.map((performance, index) => (
              <div key={index}>
                <p>Kind: {userPerformance.kind[performance.kind]}</p>
                <p>Value: {performance.value}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
