import React from "react";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  ResponsiveContainer,
} from "recharts";

const PerformanceRadarChart = ({ performanceData, kind }) => {
  const formattedData = performanceData.map((item) => ({
    ...item,
    kind: kind[item.kind],
  }));

  return (
    <ResponsiveContainer width="100%" height="100%">
      <RadarChart cx="50%" cy="50%" outerRadius="60%" data={formattedData}>
        <PolarGrid />
        <PolarAngleAxis
          dataKey="kind"
          fontSize={8} // Vous pouvez aussi utiliser "8px" si nécessaire
          tick={{ fill: "white" }} // Définit la couleur du texte des étiquettes
        />
        <Radar
          name="Performance"
          dataKey="value"
          stroke="#ff0000"
          fill="#ff0000"
          fillOpacity={0.6}
        />
        {/* Exemple de texte avec 'session' sur une autre ligne */}
        <text x="10" y="20" fill="lightgrey" textAnchor="start" fontSize={10}>
          Durée moyenne des <tspan x="10" dy="12">sessions</tspan>
        </text>
      </RadarChart>
    </ResponsiveContainer>
  );
};

export default PerformanceRadarChart;
