import React from "react";
import "./sidecard.css";

const SideCard = ({ icon, value, label, bgColor }) => {
  const sideCardsData = [
    {
      icon: icon,
      value: value,
      label: label,
      bgColor: bgColor,
    },
  ];

  return (
    <div className="side-card">
      {sideCardsData.map((card, index) => (
        <div key={index} className="side-card">
          <div className="side-card-icon" style={{ backgroundColor: card.bgColor }}>
            <img src={card.icon} alt={card.label} />
          </div>
          <div className="side-card-content">
            <h3>{card.value}</h3>
            <p>{card.label}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default SideCard;
