import axios from "axios";
import {
  USER_MAIN_DATA,
  USER_ACTIVITY,
  USER_AVERAGE_SESSIONS,
  USER_PERFORMANCE,
} from "./mockData.js"; // Assurez-vous du chemin correct

const fetchUserData = async (userId) => {
  try {
    const response = await axios.get(`/api/user/main?userId=${userId}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching user data:", error);
    throw error;
  }
};

const fetchUserActivity = async (userId) => {
  try {
    const response = await axios.get(`/api/user/activity?userId=${userId}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching user activity:", error);
    throw error;
  }
};

const fetchUserAverageSessions = async (userId) => {
  try {
    const response = await axios.get(`/api/user/average-sessions?userId=${userId}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching user average sessions:", error);
    throw error;
  }
};

const fetchUserPerformance = async (userId) => {
  try {
    const response = await axios.get(`/api/user/performance?userId=${userId}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching user performance:", error);
    throw error;
  }
};

export {
  fetchUserData,
  fetchUserActivity,
  fetchUserAverageSessions,
  fetchUserPerformance,
};
