import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";

const CustomLegend = () => {
  return (
    <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', paddingRight: 20 }}>
      <span style={{ marginRight: 15 }}>X Axis: Days</span>
      <span>Y Axis: Kilograms / Calories</span>
    </div>
  );
};

const BarChartComponent = ({ data }) => {
  // Create a formatter function for the X axis ticks
  const tickFormatter = (value, index) => {
    return index + 1; // Display 1, 2, 3, etc.
  };

  return (
    <div style={{ position: 'relative', width: '100%', height: '400px' }}>
      <div style={{ position: 'absolute', top: 0, left: 0, padding: 10 }}>
        <h3>Activit√©s quotidiennes</h3>
      </div>
      <div style={{ position: 'absolute', top: 0, right: 0, padding: 10 }}>
        <CustomLegend />
      </div>
      <ResponsiveContainer width="100%" height="100%">
        <BarChart data={data} barGap={8} barCategoryGap={1}>
          <CartesianGrid vertical={false} strokeDasharray="1 1" />
          <XAxis
            dataKey="day"
            tickLine={false}
            tick={{ fontSize: 14 }}
            dy={10}
            stroke="1 1"
            tickFormatter={tickFormatter}
          />
          <YAxis
            yAxisId="kilogram"
            dataKey="kilogram"
            type="number"
            tickCount="4"
            axisLine={false}
            orientation="right"
            tickLine={false}
            tick={{ fontSize: 14 }}
            dx={20}
            domain={["dataMin - 2", "dataMax + 1"]}
          />
          <YAxis
            yAxisId="calories"
            dataKey="calories"
            type="number"
            domain={["dataMin - 20", "dataMax + 10"]}
            hide={true}
          />
          <Tooltip />
          <Bar
            yAxisId="kilogram"
            dataKey="kilogram"
            fill="#282D30"
            barSize={7}
            radius={[50, 50, 0, 0]}
          />
          <Bar
            yAxisId="calories"
            dataKey="calories"
            fill="#E60000"
            barSize={7}
            radius={[50, 50, 0, 0]}
          />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default BarChartComponent;
