// services/apiBackend.js
import axios from 'axios';

const API_URL = "http://localhost:3000";

const fetchFromBackend = async (endpoint) => {
  try {
    const response = await axios.get(`${API_URL}${endpoint}`);
    console.log(`Fetched data from ${endpoint}:`, response.data);
    return response.data;
  } catch (error) {
    console.error(`Error fetching data from ${endpoint}:`, error);
    throw new Error(error.response ? error.response.data : error.message);
  }
};

const getUserData = (userId) => {
  return fetchFromBackend(`/user/${userId}`);
};

const getUserActivity = (userId) => {
  return fetchFromBackend(`/user/${userId}/activity`);
};

const getUserAverageSessions = (userId) => {
  return fetchFromBackend(`/user/${userId}/average-sessions`);
};

const getUserPerformance = (userId) => {
  return fetchFromBackend(`/user/${userId}/performance`);
};

export {
  getUserData,
  getUserActivity,
  getUserAverageSessions,
  getUserPerformance,
};
