import React from "react";
import { RadialBarChart, RadialBar, ResponsiveContainer } from "recharts";

const RadialBarChartScore = ({ score }) => {
  const data = [
    {
      name: "Score",
      uv: score * 100,
      fill: "#ff0000",
    },
  ];

  const endAngle = 90 + 360 * score;

  return (
    <ResponsiveContainer width="100%" height="100%">
      <RadialBarChart
        cx="50%"
        cy="50%"
        innerRadius="70%"
        outerRadius="80%"
        barSize={10}
        data={data}
        startAngle={90}
        endAngle={endAngle} // Utiliser l'angle calculé ici
      >
        <text x="10" y="10" fontSize="12">
          Score
        </text>
        <RadialBar minAngle={15} background clockWise dataKey="uv" />

        <text
          x="50%"
          y="50%"
          textAnchor="middle"
          dominantBaseline="middle"
          className="progress-label"
          style={{ fontSize: 18, fontWeight: "bold" }}
        >
          {`${data[0].uv}%`}
        </text>
        <text
          x="50%"
          y="60%"
          textAnchor="middle"
          dominantBaseline="middle"
          className="progress-legend"
          style={{ fontSize: 12, fill: "#888888" }} // Gris pour le texte de la légende
        >
          <tspan x="50%" dy="0">
            de votre
          </tspan>
          <tspan x="50%" dy="1.2em">
            objectif
          </tspan>
        </text>
      </RadialBarChart>
    </ResponsiveContainer>
  );
};

export default RadialBarChartScore;
